import React from "react";
import { insertShapes } from "../utilities/insert-shapes";
import styles from "./tac-ops-card.module.css";

const mockData = {
  title: "UPLOAD VIRAL CODE",
  text: "You can reveal this Tac Op in the Target Reveal step of any Turning Point.",
  points: [
    "If a friendly operative performs the Upload Viral Code action, you score 1VP.",
    "If you achieve the first condition in any subsequent Turning Points, you score 1VP.",
  ],
  actions: [
    {
      title: "UPLOAD VIRAL CODE",
      cost: "2AP",
      text: "An operative can perform this action while within --PENTAGON-- of your opponent's drop zone and not within --TRIANGLE-- of enemy operatives.",
    },
  ],
};

interface TacOpsAction {
  title: string;
  pretext: string;
  cost: string;
  text: string;
  q34w5ea;
}

interface TacOpsCard {
  title: string;
  text: string;
  points: string[];
  actions?: TacOpsAction[];
}

interface TacOpsCardProperties {
  cardInfo: TacOpsCard;
}

export function TacOpsCard({ cardInfo }: TacOpsCardProperties) {
  return (
    <div className={styles.container}>
      <h2 className={styles.title}>{mockData.title}</h2>
      <p>{mockData.text}</p>
      <ul>
        {mockData.points.map((point, i) => (
          <li key={i}>{point}</li>
        ))}
      </ul>
      {mockData.actions.length > 0 &&
        mockData.actions.map((action) => (
          <React.Fragment key={action.title}>
            <h2 className={styles.actionTitle}>
              <span>{action.title}</span>
              <span>{action.cost}</span>
            </h2>
            <p>{insertShapes(action.text)} </p>
          </React.Fragment>
        ))}
    </div>
  );
}
