import React from "react";
import { TacOpsCard } from "../components/tac-ops-card";

export function Home() {
  const closeCard = () => {
    console.log("CLOSE");
  };
  return (
    <div>
      <TacOpsCard
        onClose={closeCard}
        cardInfo={{
          title: "UPLOAD VIRAL CODE",
          text: "You can reveal this Tac Op in the Target Reveal step of any Turning Point.",
          points: [
            "If a friendly operative performs the Upload Viral Code action, you score 1VP.",
            "If you achieve the first condition in any subsequent Turning Points, you score 1VP.",
          ],
          actions: [
            {
              pretext:
                "Friendly operatives can perform the following mission action:",
              title: "UPLOAD VIRAL CODE",
              cost: "2AP",
              text: "An operative can perform this action while within --TRIANGLE-- of your opponent's drop zone and not within --PENTAGON-- of enemy operatives.",
            },
          ],
        }}
      />
    </div>
  );
}
